function ProcessResponseData(){

    var bodyDs = result.findDataset('records');
 //    var bodyDs = result;   

    //creamos distintos datasets segun el tipo de un parametro

    var datosMuebles = new com.konylabs.middleware.dataobject.Dataset("datosMuebles");
    var datosComponentes = new com.konylabs.middleware.dataobject.Dataset("datosComponentes");

    var sizeOfRecords = bodyDs.getAllRecords().length;
    var tempTipo = "";
    var registroDSBody = "";
    var RegistrosComponentes = new java.util.ArrayList();
    var RegistrosMuebles = new java.util.ArrayList();

  logger.debug("EKXW");

    for (i = 0; i< sizeOfRecords; i++) {

        registroDSBody = new com.konylabs.middleware.dataobject.Record();
        registroDSBody = bodyDs.getRecord(i);
        tempTipo = registroDSBody.getParam("tipo").getValue();

       if (tempTipo == "mueble" ) {
        RegistrosMuebles.add(registroDSBody);
       }else if (tempTipo == "componente" ){
        RegistrosComponentes.add(registroDSBody);
       }

    }

    datosMuebles.setRecords(RegistrosMuebles);
    datosComponentes.setRecords(RegistrosComponentes);

    var varListadoRegistros = new  java.util.ArrayList();
    varListadoRegistros.add(datosMuebles);
    varListadoRegistros.add(datosComponentes);
    //puedes aÃ±adir el cuerpo 
   // varListadoRegistros.add(bodyDs);


   result.setDataSets(varListadoRegistros);
}
ProcessResponseData();
